// import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
// import About from './components/About';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import Alert from './components/Alert';

// let name = 'Omkar'
function App() {

  const [mode, setMode] = useState('light');//checks if dark mode is enabled or not
  const [alert, setAlert] = useState(null)

const showAlert = (message,type) =>{

  setAlert({
    msg:message,
    type :type

  })
}

  const toggleMode = () =>{

    if(mode === 'light')
    {
      setMode('dark')
      document.body.style.backgroundColor = '#042743'
      showAlert("Dark Mode has been Enabled","alert-success")
    }
   else
    {
      setMode('light')
      document.body.style.backgroundColor = 'white'
      showAlert("light Mode has been Enabled","alert-success")
    }
  }
  return (
    <>

  <Navbar title = "TextUtils" aboutText = "About" mode={mode} toggleMode ={toggleMode}/>
  <div className="container my-3">

    <Alert alert = {alert}/>

  <TextForm heading = "Enter the Text below to Analyse"  mode={mode} />

  {/* <About/> */}

  </div>


  </>

  );
}

export default App;
