// import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import About from './components/About';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import Alert from './components/Alert';
import {
  BrowserRouter as Router,
  Routes,
  Route
} from "react-router-dom";


// let name = 'Omkar'
function App() {

  const [mode, setMode] = useState('light');//checks if dark mode is enabled or not
  const [alert, setAlert] = useState(null)

const showAlert = (message,type) =>{

  setAlert({
    msg:message,
    type :type

  })

  setTimeout(() => {
    setAlert(null)
  }, 1000);
}

  const toggleMode = () =>{

    if(mode === 'light')
    {
      setMode('dark')
      document.body.style.backgroundColor = '#042743'
      showAlert("Dark Mode has been Enabled",'success')
      document.title = 'Text Utils - Dark Mode'
    }
   else
    {
      setMode('light')
      document.body.style.backgroundColor = 'white'
      showAlert("Light Mode has been Enabled",'primary')
      document.title = 'Text Utils - Light Mode'
    }
  }
  return (
    <>
    <Router>

  <Navbar title = "TextUtils" aboutText = "About" mode={mode} toggleMode ={toggleMode}/>
  <div className="container my-3">


    <Alert alert = {alert}/>
{/* <Router>
    <Routes>
          <Route path="/" element = {<TextForm  showAlert = {showAlert} heading = "Enter the Text below to Analyse"  mode={mode} />}/>
          <Route path="/about" element = {<About/>}/>
    </Routes>
</Router> */}

<Routes>
            <Route exact path="/about" element={<About/>} >

            </Route>

            <Route exact path="/" element={<TextForm showAlert={showAlert} heading="Enter your text to Analyse below" mode={mode}/>}>
            </Route>
</Routes>

  </div>
  </Router>

</>

);
}

export default App;
